import React, { useState, useEffect } from 'react';
import './App.css';
import { BrowserRouter as Router } from 'react-router-dom';

function Home() {
  const [showContact, setShowContact] = useState(false);
  const [showBackground, setShowBackground] = useState(false);
  const [timerId, setTimerId] = useState(null); // Store the timer ID

  const toggleBackground = () => {
    // If background is already showing, we hide it immediately
    if (showBackground) {
      clearTimeout(timerId); // Clear the existing timer
      setShowBackground(false); // Hide the background
    } else {
      // Show the background
      setShowBackground(true);
      // Set a new timer to hide the background after 5 seconds
      const id = setTimeout(() => {
        setShowBackground(false);
      }, 5000);
      setTimerId(id); // Store the new timer ID
    }
  };

  // Clean up the timer when the component unmounts or showBackground changes
  useEffect(() => {
    return () => {
      if (timerId) clearTimeout(timerId);
    };
  }, [timerId]);

  return (
    <div className="App">
      <header className="App-header">
        <a href="https://instagram.com">
          <img
            src={`${process.env.PUBLIC_URL}/logo.png`}
            className="App-logo"
            alt="logo"
          />
        </a>
        <div className="contact-container">
          <span
            onClick={() => setShowContact(!showContact)}
            className="App-contact-text"
          >
            CONTACT
          </span>
          {showContact && (
            <div className="contact-info">
              <p>info@willow.cc</p>
            </div>
          )}
        </div>
      </header>

      <div className={`App-background ${showBackground ? "show" : ""}`}></div>

      <img
        src={`${process.env.PUBLIC_URL}/tree.png`}
        className="Tree-image"
        alt="Tree"
        onClick={toggleBackground} // Adjusted to handle both showing and hiding instantly
      />

      <footer className="App-footer">
        <p>&copy; 2024 WILLO - All Rights Reserved.</p>
      </footer>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Home />
    </Router>
  );
}

export default App;
