import React, { useState, useEffect } from 'react';
import './App.css';
import { BrowserRouter as Router } from 'react-router-dom';

function Home() {
  const [showContact, setShowContact] = useState(false);
  const [showBackground, setShowBackground] = useState(false);
  const [timerId, setTimerId] = useState(null); // Store the timer ID

  useEffect(() => {
    return () => {
      if (timerId) clearTimeout(timerId); // Clean up the timer when component unmounts
    };
  }, [timerId]); // React to changes in timerId

  const toggleBackground = () => {
    if (showBackground) {
      clearTimeout(timerId); // Clear existing timer if the background is currently shown
      setShowBackground(false); // Hide the background immediately
    } else {
      setShowBackground(true); // Show the background
      const newTimerId = setTimeout(() => {
        setShowBackground(false); // Hide the background after 5 seconds
      }, 5000);
      setTimerId(newTimerId); // Store the new timer ID
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <a href="https://instagram.com">
          <img
            src={`${process.env.PUBLIC_URL}/logo.png`}
            className="App-logo"
            alt="logo"
          />
        </a>
        <div className="contact-container">
          <span
            onClick={() => setShowContact(!showContact)}
            className="App-contact-text"
          >
            CONTACT
          </span>
          {showContact && (
            <div className="contact-info">
              <p>info@willow.cc</p>
            </div>
          )}
        </div>
      </header>

      <div className={`App-background ${showBackground ? "show" : ""}`}></div>

      <img
        src={`${process.env.PUBLIC_URL}/tree.png`}
        className="Tree-image"
        alt="Tree"
        onClick={toggleBackground} // This should toggle the background properly
      />

      <footer className="App-footer">
        <p>&copy; 2024 WILLO - All Rights Reserved.</p>
      </footer>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Home />
    </Router>
  );
}

export default App;
